#!/usr/bin/env python3import osimport sysimport timeimport yamlROOT = os.path.dirname(os.path.dirname(__file__))SRC = os.path.join(ROOT, "src")if SRC not in sys.path:    sys.path.insert(0, SRC)from bench_datasets.platinum import load_datasetfrom models.ollama import setup_model, generatewith open(os.path.join(ROOT, "experiments", "experiment.yaml"), "r", encoding="utf-8") as f:    CONFIG = yaml.safe_load(f)setup_model(CONFIG.get("model", {}))items = load_dataset(CONFIG)print(f"Loaded {len(items)} items")for i, it in enumerate(items[:3], 1):  # first 3 for a quick smoke    print(f"\n--- Item {i} [{it['id']}] ---")    print("PROMPT:", it["prompt"])    t0 = time.perf_counter()    out = generate(it["prompt"])    dt_ms = (time.perf_counter() - t0) * 1000    print("OUTPUT:", out)    print(f"LATENCY: {dt_ms:.1f} ms")    print("GOLD:", it.get("gold", ""))